}

coding_questions.code_c:
#include <stdio.h>

int main() {
    int n = 5;
    int fact = 1;
    for (int i = 1; i <= n; ++i) {
        fact *= i;
    }
    printf("Factorial of %d is: %d", n, fact);
    return 0;
}

coding_questions.code_java:
public class Fibonacci {
    public static void main(String[] args) {
        int n = 10;
        int a = 0, b = 1;
        System.out.println("Fibonacci series up to " + n + " terms:");
        for (int i = 1; i <= n; i++) {
            System.out.println(a);
            int temp = a + b;
            a = b;
            b = temp;
        }
    }
}

