#include <iostream>
#include <algorithm>

using namespace std;

int main() {
    int arr[] = {5, 2, 7, 1, 8};
    int n = sizeof(arr) / sizeof(arr[0]);
    sort(arr, arr + n);
    cout << "Sorted array: ";
    for (int i = 0; i < n; i++) {
        cout << arr[i] << " ";
    }
    return 0;
}

